mapper.select:
SELECT m.id_ AS id, m.room_id_ AS roomID, m.uid_ AS uid, u.nickname_ AS sender, m.type_ AS type, m.content_ AS content, m.instant_ AS instant FROM t_messages AS m LEFT JOIN t_users AS u ON m.uid_ = u.uid_ WHERE m.room_id_ = #{roomID}
AND m.instant_ <= #{instant}
ORDER BY m.instant_ DESC LIMIT #{size}
